-- Ideas for Book 3: إعلام الموقعين عن رب العالمين
-- Science of fatwa and principles of Islamic jurisprudence
INSERT INTO
    Idea (id, title, description, book_id, chapter_id)
VALUES
(
        40,
        'مصادر التشريع الإسلامي',
        'يقوم التشريع الإسلامي على أربعة مصادر أساسية متدرجة في قوة الحجية والاعتبار. أولها وأعظمها القرآن الكريم، كلام الله المنزل على رسوله محمد ﷺ، وهو المصدر الأول والأصل في التشريع، محفوظ من التحريف والتبديل، وما جاء فيه من أحكام فهو قطعي الثبوت والدلالة. وثانيها السنة النبوية الشريفة، وهي ما صح عن النبي ﷺ من قول أو فعل أو تقرير، وهي المبينة للقرآن والمفصلة لأحكامه والمضيفة إليه، وقد قال تعالى: "وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا". وثالثها الإجماع، وهو اتفاق المجتهدين من أمة محمد ﷺ في عصر من العصور على حكم شرعي، وهو حجة لقوله ﷺ: "إن أمتي لا تجتمع على ضلالة". ورابعها القياس، وهو إلحاق فرع بأصل لاشتراكهما في علة الحكم، كقياس النبيذ على الخمر في التحريم لعلة الإسكار. وهذه المصادر الأربعة هي التي اتفقت عليها الأمة، وأما ما سواها من المصادر المختلف فيها كالمصالح المرسلة والاستحسان وشرع من قبلنا فلها تفصيلات وضوابط خاصة. وهذا التدرج في المصادر يضمن شمولية التشريع الإسلامي ومرونته وقدرته على استيعاب كل القضايا والمستجدات مع المحافظة على الثوابت والأصول.',
        3,
        14
    ),
    (
        41,
        'قواعد فهم النصوص',
        'وضع العلماء قواعد دقيقة ومحكمة لفهم النصوص الشرعية من الكتاب والسنة، تضمن الوصول إلى المراد الإلهي والمعنى النبوي الصحيح وتجنب الخطأ في الفهم والتأويل. ومن أهم هذه القواعد النظر في سبب النزول للآية أو سبب ورود الحديث، فهو يوضح المعنى المراد ويزيل الإشكال. ومنها النظر في سياق النص وما قبله وما بعده، فالسياق قرينة مهمة في فهم المعنى المراد. ومنها حمل المطلق على المقيد والعام على الخاص عند وجود دليل على التقييد أو التخصيص. ومنها التفريق بين النص الصريح والنص الظاهر والنص المؤول، فلكل منها درجة في الدلالة. ومنها مراعاة قواعد اللغة العربية وأساليبها في التعبير، فالقرآن نزل بلسان عربي مبين والنبي ﷺ كان أفصح العرب. ومنها النظر في المقاصد الشرعية العامة من حفظ الدين والنفس والعقل والنسل والمال. ومنها الجمع بين النصوص المختلفة في الظاهر والترجيح عند تعذر الجمع وفق ضوابط علمية دقيقة. وهذه القواعد تضمن سلامة الفهم وصحة الاستنباط وحماية النصوص من التحريف والتأويل الفاسد.',
        3,
        14
    ),
    (
        42,
        'شروط الاجتهاد',
        'الاجتهاد في الشريعة الإسلامية له شروط صارمة ومؤهلات عالية لا بد من توفرها في المجتهد ليكون أهلاً لاستنباط الأحكام الشرعية من أدلتها. ومن أهم هذه الشروط معرفة اللغة العربية وعلومها من نحو وصرف وبلاغة ومعاني، لأن القرآن والسنة جاءا بالعربية. ومنها حفظ القرآن الكريم أو معرفة آيات الأحكام منه مع فهم معانيها وأسباب نزولها. ومنها معرفة السنة النبوية وخاصة الأحاديث المتعلقة بالأحكام، ومعرفة صحيحها من ضعيفها وناسخها من منسوخها. ومنها معرفة أقوال الصحابة والتابعين ومواضع الإجماع والخلاف بين العلماء. ومنها معرفة أصول الفقه وقواعده ومقاصد الشريعة العامة. ومنها العدالة والتقوى والورع الذي يمنعه من القول على الله بغير علم. ومنها الذكاء وقوة الاستنباط والقدرة على الترجيح بين الأدلة المتعارضة. ومنها معرفة أحوال الناس ومشاكلهم وواقعهم ليكون اجتهاده مناسباً لزمانه ومكانه. وهذه الشروط تضمن أن يكون الاجتهاد صحيحاً مبنياً على علم وبصيرة وليس على هوى أو جهل، وتحفظ الشريعة من التلاعب والتحريف.',
        3,
        14
    ),
    (
        43,
        'حفظ الضروريات الخمس',
        'وضع الإسلام نظاماً محكماً لحفظ الضروريات الخمس التي لا تستقيم حياة الإنسان بدونها، وهي أساس كل تشريع وعلة كل حكم. أولها حفظ الدين، فهو أعظم الضروريات وأشرفها، لأنه صلة العبد بربه ومعرفته بخالقه وسبيل سعادته في الدنيا والآخرة، ولذلك شرع الجهاد لحمايته وعقوبة الردة لحفظه. وثانيها حفظ النفس الإنسانية التي كرمها الله وجعل قتلها كقتل الناس جميعاً، فحرم القتل وشرع القصاص والديات وأباح الدفاع عن النفس. وثالثها حفظ العقل الذي يميز الإنسان عن سائر المخلوقات وبه التكليف والمسؤولية، فحرم كل ما يذهبه كالخمر والمخدرات وشرع عقوبات رادعة. ورابعها حفظ النسل لاستمرار النوع الإنساني وعمارة الأرض، فشرع الزواج وحرم الزنا واللواط وكل ما يضر بالنسل والذرية. وخامسها حفظ المال لأنه عصب الحياة وأساس المعيشة، فحرم السرقة والغصب والربا وشرع عقوبات حازمة. وهذه الضروريات الخمس محفوظة بطريقين: الوجود وهو تشريع ما يحققها ويثبتها، والعدم وهو منع ما يضرها ويزيلها، وبهذا تكتمل منظومة التشريع الإسلامي.',
        3,
        15
    ),
    (
        44,
        'المصالح والمفاسد',
        'بنى الإسلام منهجاً دقيقاً ومحكماً في الموازنة بين المصالح والمفاسد، يقوم على قواعد عقلية وشرعية راسخة تضمن تحقيق أعظم المنافع ودفع أكبر المضار. والأصل في هذا المنهج أن الشريعة جاءت بتحصيل المصالح وتكميلها وتعطيل المفاسد وتقليلها، فإذا تعارضت مصلحة ومفسدة قُدمت المصلحة الراجحة ودُفعت المفسدة الأكبر. وإذا تعارضت مصالح متعددة قُدمت الأعظم فالأعظم، فالضروريات مقدمة على الحاجيات والحاجيات على التحسينيات، ومصالح الدين مقدمة على مصالح الدنيا، ومصالح الجماعة مقدمة على مصالح الفرد عند التعارض. وإذا تعارضت مفاسد متعددة ارتُكب أخفها لدفع أعظمها، كما يُتحمل الضرر الأصغر لدفع الضرر الأكبر. ومن قواعد هذا المنهج أن المفسدة إذا كانت أعظم من المصلحة مُنعت المصلحة، وأن الضرر لا يُزال بضرر مثله أو أعظم منه، وأن الضرر الأشد يُزال بالضرر الأخف. وهذا المنهج يجعل المجتهد قادراً على إصدار أحكام متوازنة ومناسبة لكل زمان ومكان، ويحقق مقاصد الشريعة في جلب المصالح ودرء المفاسد.',
        3,
        15
    ),
    (
        45,
        'تحقيق مصالح العباد',
        'إن الحكمة العظمى من إنزال الشريعة الإسلامية هي تحقيق مصالح العباد في الدنيا والآخرة، فقد جاءت رحمة للعالمين وهداية للبشرية جمعاء، تسعى لإسعادهم وإصلاح أحوالهم وتنظيم علاقاتهم. وهذه المصالح شاملة متكاملة، تشمل مصالح الفرد والجماعة، ومصالح الدنيا والآخرة، ومصالح الروح والجسد، ومصالح العقل والقلب. فالشريعة لم تهمل جانباً من جوانب الحياة الإنسانية، بل نظمت العبادات لتحقيق الصلة بالله وتزكية النفس، ونظمت المعاملات لتحقيق العدل والتعاون بين الناس، ونظمت الأخلاق لتحقيق السمو والكمال الإنساني، ونظمت العقوبات لحماية المجتمع ومنع الظلم والفساد. والمقصود بالمصلحة في الشرع ما يحقق منفعة للعبد أو يدفع عنه مضرة في دينه ودنياه وآخرته، سواء أدرك ذلك بعقله أم لا، لأن علم الله محيط وحكمته بالغة. ولهذا كانت أحكام الشريعة كلها مصالح، حتى ما قد يبدو في ظاهره شاقاً أو صعباً، فهو في حقيقته مصلحة للعبد وخير له في دينه ودنياه. وهذا ما يجعل المؤمن يستسلم لأحكام الله ويثق في حكمته، لأنه يعلم أن كل ما شرعه الله إنما هو لخيره ومصلحته.',
        3,
        15
    ),
    (
        46,
        'أركان القياس الصحيح',
        'القياس في الشريعة الإسلامية عبارة عن إلحاق فرع بأصل لاشتراكهما في علة الحكم، وهو عملية منطقية دقيقة لها أربعة أركان أساسية لا يصح بدونها. أولها الأصل، وهو القضية المعلومة الحكم بنص أو إجماع، فلا يصح القياس على أصل مختلف فيه أو غير معلوم الحكم. وثانيها الفرع، وهو القضية الجديدة التي يُراد معرفة حكمها من خلال إلحاقها بالأصل، ويشترط أن يكون مجهول الحكم ومتفقاً مع الأصل في العلة. وثالثها العلة، وهي الوصف المشترك بين الأصل والفرع الذي يناط به الحكم، ويشترط فيها أن تكون منضبطة ومعقولة ومناسبة للحكم وغير معارضة لنص أو إجماع. ورابعها الحكم، وهو الأثر الشرعي المترتب على وجود العلة، ويشترط أن يكون معقولاً ومناسباً للعلة وغير معارض لنص أو إجماع. ومثال ذلك قياس النبيذ على الخمر في التحريم، فالأصل هو الخمر المحرم بالنص، والفرع هو النبيذ غير المنصوص على حكمه، والعلة هي الإسكار المشترك بينهما، والحكم هو التحريم. وبهذه الأركان الأربعة يصبح القياس صحيحاً وحجة شرعية يُعتمد عليها في استنباط الأحكام.',
        3,
        16
    ),
    (
        47,
        'شروط صحة القياس',
        'وضع علماء أصول الفقه شروطاً دقيقة ومحكمة لصحة القياس وقبوله كحجة شرعية، تضمن سلامة الاستنباط وصواب النتيجة. أول هذه الشروط أن يكون الأصل ثابت الحكم بنص قطعي أو إجماع متيقن، فلا يقاس على ما هو مختلف فيه أو منسوخ. وثانيها وجود العلة الصحيحة المنضبطة في الأصل، بحيث تكون معقولة وظاهرة ومناسبة للحكم وغير معارضة لنص أو إجماع. وثالثها أن يكون الفرع مجهول الحكم خالياً من نص يبين حكمه، وموافقاً للأصل في وجود العلة. ورابعها عدم وجود معارض للقياس من نص أو إجماع أو قياس أقوى منه، فإن وجد معارض أقوى سقط القياس أو رُجح عليه. وخامسها أن يكون حكم الأصل معقول المعنى وليس تعبدياً محضاً، فالعبادات لا يُقاس عليها. وسادسها عدم كون الفرع أولى بالحكم من الأصل، إذ لو كان أولى لكان هو الأصل وما سُمي أصلاً هو الفرع. وهذه الشروط بمجملها تضمن قوة القياس وصحته وقبوله في الفقه الإسلامي، وتحمي الشريعة من القياسات الفاسدة والاستنباطات الخاطئة.',
        3,
        16
    ),
    (
        48,
        'أنواع القياس',
        'ينقسم القياس في الشريعة الإسلامية إلى ثلاثة أنواع رئيسية بناء على قوة العلة في الفرع مقارنة بالأصل، ولكل نوع خصائصه وقوته في الاستدلال. أولها قياس الأولى، وهو أقوى أنواع القياس وأوضحها، يكون فيه الفرع أولى بالحكم من الأصل لقوة العلة فيه، مثل قياس ضرب الوالدين على التأفيف لهما، فإذا كان التأفيف محرماً فالضرب أولى بالتحريم. وثانيها قياس المساواة، وهو ما استوى فيه الفرع والأصل في قوة العلة، فيكون الحكم واحداً فيهما دون تفاضل، مثل قياس العصير على الخمر في التحريم لأن كليهما مُسكر. وثالثها قياس الأدنى، وهو أضعف أنواع القياس، يكون فيه الفرع أضعف في العلة من الأصل، لكن يبقى فيه جامع معتبر، مثل قياس العيب اليسير في البيع على العيب الفاحش في وجوب الخيار. وهذا التقسيم يساعد الفقيه في ترتيب القياسات بحسب قوتها وترجيح بعضها على بعض عند التعارض، فقياس الأولى مقدم على قياس المساواة، وقياس المساواة مقدم على قياس الأدنى، كما يساعد في فهم طبيعة كل قياس وقوته الاستدلالية.',
        3,
        16
    ),
    (
        49,
        'حقيقة الاستحسان',
        'الاستحسان في الفقه الإسلامي هو عبارة عن ترك القياس الجلي الظاهر إلى قياس خفي أو دليل أقوى منه، وهو منهج فقهي دقيق يهدف إلى تحقيق العدالة ورفع الحرج والمشقة عن الناس. ويقوم هذا المبدأ على أن المجتهد قد يجد نفسه أمام قياس ظاهر يقتضي حكماً معيناً، لكن هناك دليل آخر أقوى منه يقتضي حكماً مخالفاً، فيترك الأول ويأخذ بالثاني استحساناً له. وهذا لا يعني التحكم أو الهوى، بل هو اتباع لأقوى الأدلة وأوفقها لمقاصد الشريعة. وقد عرفه بعض العلماء بأنه العدول عن موجب القياس إلى ما هو أولى منه لدليل معتبر. والاستحسان ليس مصدراً مستقلاً للتشريع، بل هو طريقة في الاستدلال والترجيح بين الأدلة المتعارضة. ومن أمثلة ذلك استحسان الإمام أبي حنيفة في مسألة وضوء المستحاضة، حيث ترك القياس على الحائض في عدم وجوب الوضوء، وأوجب عليها الوضوء استحساناً لأن حالها قد يطول ويصعب عليها أداء العبادات. وهذا يظهر مرونة الفقه الإسلامي وقدرته على مواجهة الواقع وحاجات الناس بما يحقق مصالحهم ويرفع عنهم الحرج.',
        3,
        17
    ),
    (
        50,
        'ضوابط المصالح المرسلة',
        'المصالح المرسلة هي المنافع التي لم يرد في الشرع نص بعينه يعتبرها أو يلغيها، لكنها تتفق مع مقاصد الشريعة العامة وتحقق مصالح العباد. ولاعتبار هذه المصالح وضع العلماء ضوابط دقيقة ومحكمة تضمن عدم الخروج عن نهج الشريعة ومقاصدها. أول هذه الضوابط أن تكون المصلحة حقيقية لا وهمية، بمعنى أن تجلب نفعاً حقيقياً أو تدفع ضرراً محققاً. وثانيها أن تكون عامة لا خاصة، بمعنى أن تحقق مصلحة للجماعة وليس لفرد أو فئة معينة. وثالثها أن لا تعارض نصاً قطعياً أو إجماعاً صحيحاً، فإن عارضت نصاً أو إجماعاً فهي فاسدة غير معتبرة. ورابعها أن تكون معقولة في ذاتها، بحيث يدركها العقل السليم ويقر بها. وخامسها أن تندرج تحت مقصد من مقاصد الشريعة الخمسة وهي حفظ الدين والنفس والعقل والنسل والمال. وسادسها أن تكون في رتبة الضروريات أو الحاجيات، وليست مجرد تحسينيات أو كماليات. وهذه الضوابط تجعل المصالح المرسلة وسيلة فعالة لمواجهة المستجدات والنوازل التي لم ينص عليها بعينها، مع المحافظة على روح الشريعة ومقاصدها.',
        3,
        17
    ),
    (
        51,
        'أمثلة على الاستحسان',
        'وردت في الفقه الإسلامي أمثلة عديدة توضح تطبيق مبدأ الاستحسان وتظهر حكمة الشارع في مراعاة أحوال الناس ومصالحهم. من أشهر هذه الأمثلة ما فعله الإمام أبو حنيفة في مسألة المستحاضة، حيث أوجب عليها الوضوء استحساناً رغم أن القياس يقتضي عدم وجوبه قياساً على الحائض، لأن حالها قد يطول ويشق عليها أداء العبادات. ومنها أيضاً مسألة عقد الاستصناع، حيث أجاز العلماء بيع الشيء قبل وجوده في العقود المحتاج إليها والمهمة للناس، استحساناً لحاجة الناس إليها وعموم البلوى بها، رغم أن القياس يقتضي عدم جوازها لأنها بيع معدوم. ومنها أيضاً مسألة الوقف، حيث أجاز العلماء وقف العقار على الخير استحساناً لما فيه من مصلحة عامة وخير للمجتمع، رغم أن القياس قد يقتضي غير ذلك. ومنها مسألة الحضانة، حيث رأى بعض العلماء أن بنت الأخ من الرضاعة لا تحضن ابن أخيها استحساناً لما في ذلك من رعاية مشاعر الأسرة ومصلحة الأطفال. وهذه الأمثلة تظهر أن الاستحسان ليس تحكماً أو هوى، بل هو اتباع للدليل الأقوى وتحقيق لمقاصد الشريعة في جلب المصالح ودرء المفاسد.',
        3,
        17
    ),
    (
        52,
        'مفهوم سد الذرائع',
        'سد الذرائع هو مبدأ فقهي عظيم يقوم على منع الوسائل والطرق والأسباب التي تفضي إلى المحرمات أو تُوصل إليها، حتى ولو لم تكن هذه الوسائل محرمة في ذاتها. وهذا المبدأ ينطلق من قاعدة عقلية وشرعية مفادها أن الوسيلة تأخذ حكم الغاية، فإذا كانت الغاية محرمة فالوسيلة إليها محرمة أيضاً. وهذا المفهوم يشمل عدة اعتبارات: أولها الذرائع القريبة التي تفضي غالباً إلى المحرم، وثانيها الذرائع البعيدة التي يندر أن تفضي إلى المحرم. ومن أمثلة ذلك منع بيع العنب لمن يتخذه خمراً، فالعنب حلال في ذاته لكن بيعه لمن يعصره خمراً ممنوع سداً للذريعة. ومنها أيضاً منع الخلوة بالأجنبية سداً لذريعة الزنا، ومنع مصافحة الرجال للنساء الأجنبيات سداً لذريعة الفتنة. ومنها منع سب آلهة المشركين سداً لذريعة سبهم لله تعالى. وهذا المبدأ يوضح أن الشريعة لا تنظر إلى الأفعال بمعزل عن عواقبها ونتائجها، بل تراعي المآلات والعواقب وتتخذ التدابير الوقائية لمنع الوقوع في المحظور.',
        3,
        18
    ),
    (
        53,
        'فتح الذرائع للمصالح',
        'فتح الذرائع هو الوجه الآخر لمبدأ سد الذرائع، ويقوم على فتح الطرق والوسائل وتسهيلها للوصول إلى المصالح الشرعية والمنافع الحقيقية، وإزالة العقبات والعوائق التي تحول دون تحقيقها. وهذا المبدأ يقوم على أن الشريعة لا تقتصر على منع المفاسد وحسب، بل تسعى أيضاً إلى تحقيق المصالح وتعظيمها وتسهيل الوصول إليها. ومن أمثلة ذلك إباحة التجارة وتسهيل طرقها ووسائلها لتحقيق مصلحة الكسب والرزق، وإباحة الزواج وتسهيل أسبابه لتحقيق مصلحة حفظ النسل والعفة. ومنها إباحة طلب العلم وتسهيل وسائله وطرقه لتحقيق مصلحة تنوير العقول وهداية الناس. ومنها إباحة التداوي وتسهيل الوصول إلى العلاج لتحقيق مصلحة حفظ النفس والصحة. وهذا المبدأ يظهر أن الشريعة إيجابية في نظرتها للحياة، فهي لا تكتفي بمنع الشر بل تسعى إلى نشر الخير وتعميمه، ولا تقف عند حدود المنع بل تتجاوزها إلى آفاق التنمية والبناء. وبهذا يتكامل مبدآ سد الذرائع وفتح الذرائع لتحقيق منظومة متوازنة من الأحكام تجمع بين درء المفاسد وجلب المصالح.',
        3,
        18
    ),
    (
        54,
        'ضوابط سد الذرائع',
        'وضع علماء أصول الفقه ضوابط دقيقة ومحكمة لتطبيق مبدأ سد الذرائع، تضمن عدم المغالاة فيه وعدم التوسع فيه بغير ضابط ولا مبرر. أول هذه الضوابط أن تكون الذريعة قريبة الإفضاء إلى المفسدة أو المحرم، فالذرائع البعيدة أو المحتملة احتمالاً ضعيفاً لا يُسد من أجلها. وثانيها أن تكون المفسدة المترتبة على سد الذريعة أقل من المفسدة المترتبة على فتحها، فلا يفتح باب ضرر لدفع ضرر أصغر منه. وثالثها اعتبار مقاصد الشريعة وعدم معارضتها، فلا يُسد ذريعة لمفسدة صغيرة إذا كان في ذلك تعطيل لمقصد أعظم. ورابعها مراعاة العرف والعادة في تقدير قرب الذريعة وبعدها، فما يعتبره الناس قريباً مؤدياً للمفسدة يُسد وما يعتبرونه بعيداً لا يُسد. وخامسها مراعاة ظروف الزمان والمكان والأشخاص، فقد تُسد ذريعة في زمن دون زمن أو مكان دون مكان. وهذه الضوابط تجعل مبدأ سد الذرائع أداة فعالة ومنضبطة لتحقيق مقاصد الشريعة في حفظ المصالح ودرء المفاسد.',
        3,
        18
    ),
    (
        55,
        'مرونة الشريعة',
        'مرونة الشريعة الإسلامية من أعظم خصائصها وأشرف مزاياها، وهي قدرتها على التكيف والاستجابة للظروف المتغيرة والمستجدات المختلفة، مع المحافظة على جوهرها وأصولها الثابتة. وتتجلى هذه المرونة في عدة جوانب: أولها في وجود أحكام مرنة تتغير بتغير الظروف والعلل، فما كان مشروعاً في ظرف قد يصبح ممنوعاً في ظرف آخر إذا تغيرت العلة أو المال. وثانيها في وجود مساحة واسعة للاجتهاد والاستنباط في المسائل التي لم يرد فيها نص قطعي. وثالثها في مراعاة العرف والعادة والمصلحة في الأحكام الفرعية. ورابعها في مبدأ التيسير ورفع الحرج عند الضرورة، فالضرورات تبيح المحظورات والمشقة تجلب التيسير. وخامسها في اعتبار الزمان والمكان وأحوال الناس في بعض الأحكام، فقد تختلف الفتوى في المسألة الواحدة باختلاف هذه العوامل. وهذه المرونة لا تعني التلاعب بالنصوص أو تغيير الثوابت، بل تعني التطبيق الحكيم للنصوص في ضوء الظروف والمقاصد، وهذا ما يجعل الشريعة صالحة لكل زمان ومكان وقادرة على مواجهة تحديات العصر ومشاكله.',
        3,
        19
    ),
    (
        56,
        'الثابت والمتغير',
        'يقوم الفقه الإسلامي على مبدأ مهم يميز بين ما هو ثابت وما هو متغير في الأحكام الشرعية، وهذا التمييز ضروري لفهم طبيعة الشريعة وضمان حسن تطبيقها. فالثابت يشمل عقائد الإيمان وأصول الدين وأركان الإسلام الخمسة، والقطعيات من القرآن والسنة، والمقاصد العامة للشريعة في حفظ الضروريات الخمس، والقيم الأخلاقية العليا كالعدل والصدق والأمانة. وهذه الثوابت لا تتغير بتغير الزمان والمكان ولا بتطور الحضارة أو تبدل العادات. أما المتغير فيشمل الأحكام الفرعية التي تعتمد على الاجتهاد والقياس واعتبار المصالح، والأحكام المرتبطة بالعرف والعادة، والأحكام التي تتأثر بظروف الزمان والمكان وأحوال الناس. ومن أمثلة المتغير أحكام المعاملات المالية الحديثة، وأحكام الطب والبحوث العلمية، وبعض أحكام العبادات المرتبطة بالظروف والأحوال. وهذا التقسيم يساعد العلماء على فهم طبيعة النصوص والأحكام، ويضع لهم ضوابط واضحة في التعامل مع المستجدات والقضايا المعاصرة، ويحفظ التوازن بين الثبات والمرونة في الشريعة.',
        3,
        19
    ),
    (
        57,
        'أمثلة على تغير الفتوى',
        'وردت في التاريخ الإسلامي أمثلة عديدة ومهمة على تغير الفتوى بتغير الظروف والأحوال، توضح مرونة الشريعة وقدرتها على مواجهة الواقع بحكمة وعدالة. من أشهر هذه الأمثلة ما فعله عمر بن الخطاب رضي الله عنه حين أوقف حد السرقة في عام المجاعة لما رأى أن الناس يسرقون من شدة الجوع، فاعتبر أن شرط إقامة الحد وهو عدم وجود الضرورة غير متوفر. ومنها أيضاً عدم تطبيقه لآية تأليف القلوب في الزكاة لما رأى أن الظروف تغيرت وأن الإسلام قوي ولا يحتاج لهذا النوع من التأليف. ومنها ما فعله في عدم تقسيم الأراضي المفتوحة عنوة على الفاتحين، بل تركها لأهلها يزرعونها ويدفعون الخراج، لما رأى في ذلك من مصلحة عامة للمسلمين. ومن الأمثلة المعاصرة تغير الفتوى في بعض المعاملات المالية بناء على تطور وسائل التجارة والاتصال، وتغير بعض أحكام العبادات في ظروف استثنائية كالحروب أو الأوبئة، واعتبار القوانين المحلية والدولية في بعض المعاملات عندما لا تتعارض مع نص أو مقصد شرعي. وهذه الأمثلة تظهر أن تغير الفتوى ليس تلاعباً بالدين أو تحريفاً للنصوص، بل هو فهم عميق لزوح الشريعة ومقاصدها وحسن تطبيق لها بما يحقق مصالح العباد في كل زمان ومكان.',
        3,
        19
    ),