INSERT INTO Quote (content,description,example,"text",title,"type") VALUES
	 (NULL,'السعادة الحقيقية تأتي عندما يكون التعامل مع الناس نابعًا من الإخلاص لله، بعيدًا عن المصلحة الشخصية أو الخوف منهم، مما يجعل العلاقة قائمة على النية الصالحة.',' ','السعادة في معاملة الخلق أن تعاملهم لله، فترجو الله فيهم ولا ترجوهم في الله، وتخافه فيهم ولا تخافهم في الله.','السعادة والرضا',1),
	 (NULL,'الامتثال لأوامر الله، رغم صعوبته أحيانًا، أهون من تحمل العذاب الناتج عن معصيته، مما يحث على الصبر والطاعة.',NULL,'الصبر على طاعة الله أيسر من الصبر على عذابه.','الصبر والابتلاء',1),
	 (NULL,'كما يحتاج البدن إلى العلاج، يحتاج القلب إلى التوبة من الذنوب ودوام ذكر الله لتطهيره وتجديد إيمانه.',NULL,'القلب يمرض كما يمرض البدن، وشفاؤه في التوبة والحمية، ويصدأ كما تصدأ المرآة وجلاؤه بالذكر.',' أمراض القلب وشفاؤه',1),
	 (NULL,'الحب الذي ينبع من الإيمان خالص لله يجعل المحبة صادقة ونقية، على عكس الحب الذي يعتمد على المصالح الشخصية.',NULL,'من أحبك في الله أحبك لنفسك، ومن أحبك لهواك أحبك لنفسه.',' الحب في الله',1),
	 (NULL,' التوكل الصحيح يجمع بين الثقة الكاملة بالله والسعي الجاد لتحقيق الأهداف، فلا يكفي الاعتماد القلبي وحده دون العمل.',NULL,'التوكل هو صدق اعتماد القلب على الله مع الأخذ بالأسباب.',' التوكل على الله',1),
	 (NULL,'القلب يشعر بالضياع والوحشة والحزن، ولا يجد راحته إلا بالقرب من الله، والأنس بذكره وطاعته.',NULL,'إن في القلب شعثًا لا يلمه إلا الإقبال على الله، وفيه وحشة لا يزيلها إلا الأنس بالله، وفيه حزن لا يذهبه إلا السرور بمعرفته وصدق معاملته.','الذكر وطمأنينة القلب',1),
	 (NULL,'العلم النافع هو المستند إلى الوحي الإلهي وما جاء به الرسول صلى الله عليه وسلم، وليس الجدال العقيم الذي يخلط الحق بالباطل.',NULL,'العلم قال الله قال رسوله قال الصحابة ليس بالتمويه، ما العلم نصبك للخلاف سفاهةً بين الرسول وبين رأي فقيه.','العلم والعمل',1),
	 (NULL,'الزهد لا يعني التخلي عن الدنيا، بل أن يكون القلب متعلقًا بالله أكثر من تعلقه بالماديات.',NULL,'إن الزهد ليس بتحريم الحلال ولا بإضاعة المال، ولكن الزهد أن تكون بما في يد الله أوثق منك بما في يدك.','الزهد والقناعة',1),
	 (NULL,'الإخلاص هو عمل القلب الصافي لله وحده، بعيدًا عن الرياء أو التأثيرات الخارجية.',NULL,'الإخلاص سر بين العبد وربه لا يعلمه ملك فيكتبه، ولا شيطان فيفسده، ولا هوى فيميله.','الإخلاص',1),
	 (NULL,'الشكر الحقيقي يشمل التعبير عن الامتنان باللسان، والإحساس بالنعم في القلب، واستعمالها في طاعة الله.',NULL,'الشكر ظهور أثر نعمة الله على لسان عبده ثناءً واعترافًا، وعلى قلبه شُهودًا ومحبةً، وعلى جوارحه طاعةً وانقيادًا.','الشكر على النعم',1);
INSERT INTO Quote (content,description,example,"text",title,"type") VALUES
	 (NULL,'الذنوب تتسبب في اعتياد المعصية وتجلب المزيد منها، مما يجعل الابتعاد عنها أصعب بمرور الوقت.',NULL,'المعاصي تزرع أمثالها، ويولد بعضها بعضًا، حتى يعز على العبد مفارقتها والخروج منها.','أثر المعاصي على القلب',1),
	 (NULL,' الدنيا مكان مؤقت للمرور والاختبار، بينما الآخرة هي المستقر الحقيقي، فلا ينبغي أن يكون التركيز على الدنيا فقط.',NULL,'الدنيا مجاز، والآخرة وطن، والمجاز لا يُعمر.','الدنيا دار ممر',1),
	 (NULL,'الإيمان ليس مجرد أقوال أو أمنيات، بل تصديق داخلي يظهر في أفعال الإنسان وسلوكه.',NULL,'ليس الإيمان بالتحلي ولا بالتمني، ولكن ما وقر في القلب وصدّقه العمل.','الإيمان والعمل',1),
	 (NULL,'حياة القلب تتطلب أعمالاً تقوي الإيمان مثل التدبر في القرآن، العبادة، والصحبة الصالحة.',NULL,'حياة القلب في خمسة أشياء: قراءة القرآن بالتدبر، وخلو البطن، وقيام الليل، والتضرع عند السحر، ومجالسة الصالحين.',' أسباب حياة القلب',1),
	 (NULL,'المحبة الصادقة تمتد جذورها في القلب، وتظهر ثمارها في الأقوال والأفعال الصالحة.',NULL,'المحبة شجرة طيبة أصلها ثابت وفرعها في السماء، وثمارها تظهر في القلب واللسان والجوارح.',' حقيقة الحب',1),
	 (NULL,'التسامح مع الآخرين هو خلق نبيل يرفع مقام الإنسان ويقربه إلى الله.',NULL,'العفو عن الناس من أعظم أخلاق الكرماء، ومن صفات أهل التقوى.','العفو والتسامح',1),
	 (NULL,' الفتن تكون واضحة لأصحاب البصيرة في بدايتها، أما من يجهل حقيقتها فلا يدركها إلا بعد وقوع الضرر.',NULL,'الفتنة إذا أقبلت عرفها كل عاقل، وإذا أدبرت عرفها كل جاهل.','الفتنة وضررها',1),
	 (NULL,' التفكير هو بداية الأعمال، فإذا كان التفكير إيجابيًا أتى الخير، وإذا كان سلبيًا جلب الشر.',NULL,'أصل الخير والشر من قبل التفكر، فإن الفكر مبدأ الإرادة والطلب.','التربية النفسية',1),
	 (NULL,'البركة في الوقت تأتي من النية الخالصة والعمل المخلص وفق تعاليم السنة النبوية.',NULL,'بركة الوقت في حسن النية، وإخلاص العمل، ومتابعة السنة','بركة الوقت',1),
	 (NULL,'الغفلة تعمي البصيرة، وعندما يفيق الإنسان منها يدرك الحقائق التي كانت غائبة عنه.',NULL,'الغفلة نوم العقل، فإذا استيقظ أدرك الحقائق كما هي.','أثر الغفلة',1);
INSERT INTO Quote (content,description,example,"text",title,"type") VALUES
	 (NULL,'الصديق الصادق هو من يحافظ على محبتك وسمعتك في غيابك، ويدخل السرور على قلبك في حضورك، ويساندك عند الشدة.',NULL,'الصديق من إذا غبت عنه حفظك، وإذا حضرت سرك، وإذا وقعت أعانك.','الصداقة الحقيقية',1),
	 (NULL,'العمل الصالح الذي يفتقر إلى الإخلاص والاتباع للرسول صلى الله عليه وسلم يصبح عبئًا بلا فائدة.',NULL,'العمل بغير إخلاص ولا اقتداء كالمسافر يملأ جرابه رملاً يثقله ولا ينفعه.','العمل الصالح',1),
	 (NULL,'مكانة العبد عند الله تتجلى في مدى صبره عندما يُبتلى، وشكره لله عند النعم.',NULL,'إذا أردت أن تعرف قدرك عند الله، فانظر إلى صبرك عند البلاء وشكرك عند النعم.','التربية على الصبر',1),
	 (NULL,'التواضع لله يؤدي إلى رفع درجات الإنسان في الدنيا والآخرة، بينما التكبر يُذل صاحبه.',NULL,'من تواضع لله رفعه، ومن تكبر وضعه.','قيمة التواضع',1),
	 (NULL,' الاستمرار في المعاصي دون توبة يجعل الإنسان عرضة لسوء الخاتمة.',NULL,'أعظم ما يُخاف من سوء الخاتمة الإصرار على الذنب.',' حسن الخاتمة',1),
	 (NULL,'الشخص الذي يذكر الله بين الغافلين يضيء بحياته الإيمانية، تمامًا كما تبرز الشجرة الخضراء بين الأشجار الجافة.',NULL,'الذاكر لله في الغافلين كالشجرة الخضراء في وسط الشجر اليابس.',' نعمة الذكر',1),
	 (NULL,'ضياع الوقت في غير طاعة الله أشد ضررًا من الموت، لأنه يحرمك من التقرب إلى الله.
INSERT INTO Quote (content,description,example,"text",title,"type") VALUES
	 (NULL,'الإخلاص في العمل لله يجلب البركة ويُنير القلب بالإيمان، مما يجعل العمل صالحًا ومقبولًا.',NULL,'من عمل لله بالصدق، أصلح الله له عمله، وجعل له نورًا في قلبه.','الإيمان والعمل الصالح',1),
	 (NULL,'ضبط اللسان وحسن الكلام يؤدي إلى راحة النفس وتجنب الفتن والمشاكل.',NULL,'من حفظ لسانه، أراح قلبه، وسلِم من البلاء.','حفظ اللسان',1),
	 (NULL,'الإعجاب بالنفس خطر على الإخلاص، ويجب تذكر أن كل نجاح هو بفضل الله وحده.',NULL,'إذا رأيت نفسك معجبًا بعملك، فتذكر من الذي وفقك له.',' التواضع والقرب من الله',1),
	 (NULL,'الزهد لا يعني التخلي عن الدنيا تمامًا، بل أن تكون مادية الدنيا تحت السيطرة، لا تتحكم في مشاعر الإنسان وقراراته.',NULL,'الزهد أن تجعل الدنيا في يدك لا في قلبك.','الزهد الحقيقي',1),
	 (NULL,' المعاصي تؤثر سلبًا على القلب، فتُظلمه وتُشعر الإنسان بالضيق والحزن.',NULL,'الذنوب تضعف القلب، وتُذهب نوره، وتُضيق الصدر.',' أثر الذنوب على النفس',1),
	 (NULL,' الإحسان في العبادة يعني أن تعبد الله وكأنك تشعر بحضوره، مما يزيد من خشوعك وإخلاصك.',NULL,'أحسن عبادة الله كأنك تراه، فإن لم تكن تراه فإنه يراك.','الإحسان في العبادة',1),
	 (NULL,'التوبة الصادقة تجلب سعادة داخلية تفوق أي متعة دنيوية زائفة.',NULL,'التائب يجد لذة في قلبه أعظم من لذة أهل اللهو في لهوهم.','أثر التوبة على النفس',1),
	 (NULL,'الشخص المستقيم هو من يطيع الله باستمرار، في كل حالاته، سواء كان بين الناس أو بمفرده.',NULL,'الاستقامة هي أن تكون طاعتك لله متصلة في السر والعلانية.','الاستقامة على الدين',1),
	 (NULL,'ضبط النفس عن الشهوات والمعاصي يُقرب الإنسان إلى الله ويزيد من درجاته.',NULL,'من ملك شهوته، كان أقرب إلى ربه.','الشهوة وضبط النفس',1),
	 (NULL,'حب الله يجعل الإنسان يعيش مطمئنًا، بلا خوف من أي مخلوق أو ظرف.',NULL,'القلب إذا تعلق بالله، زالت عنه المخاوف كلها.',' المحبة والخوف من الله',1);
INSERT INTO Quote (content,description,example,"text",title,"type") VALUES
	 (NULL,'طلب العلم يتطلب جهدًا ومثابرة، ولا يُمكن الحصول عليه دون تعب ومجاهدة للنفس.',NULL,'العلم لا يُنال براحة الجسد.','طلب العلم',1),
	 (NULL,'التسامح يؤدي إلى تهدئة النفوس وإزالة الشحناء، مما يجلب المودة والسلام بين الناس.',NULL,'العفو يُصلح القلوب، ويُطفئ نار العداوة','العفو عن الناس',1),
	 (NULL,'ذكر الله هو غذاء الروح، وتركه يؤدي إلى جفاف القلب وموته معنويًا.',NULL,'ذكر الله للقلب كالطعام للجسد، فإذا تركه مات.','أثر ذكر الله',1),
	 (NULL,'الاستغفار المستمر يجلب الفرج من الكرب ويوسع الرزق ويزيل الهموم.',NULL,'من لزم الاستغفار، جعل الله له من كل ضيق مخرجًا ومن كل هم فرجًا.','الاستغفار وسعة الرزق',1),
	 (NULL,'حب الله يجعل الإنسان محبوبًا من خلقه، لأن محبته تنعكس على أفعاله وسلوكه.',NULL,'من أحب الله، أحب عباد الله، وأحبه عباد الله','أثر المحبة الصادقة',1),
	 (NULL,'الصدقة لا تُنقص المال، بل تزيده بالبركة والأجر الذي يعوضه الله به.',NULL,'ما نقص مال من صدقة، بل تزده تزكية وبركة.',' فضل الإنفاق',1),
	 (NULL,'الحسد يضر صاحبه فقط، حيث يفقد راحة البال في الدنيا ويُعاقب عليه في الآخرة.',NULL,'الحاسد لا ينال إلا خسارة الدنيا وحسرة الآخرة.','ترك الحسد',1),
	 (NULL,'نقاء القلب من الذنوب والمعاصي يجعله مشرقًا بالإيمان والخير.',NULL,'إذا طهُر القلب من أدران الذنوب، كان كالمصباح المنير.','صيانة القلب',1),
	 (NULL,'الإيمان الحقيقي يُبنى على يقين صادق، ويُغذى بالإخلاص، وينتج عنه أعمال صالحة.',NULL,'الإيمان شجرة أصلها اليقين، وساقها الإخلاص، وثمارها العمل الصالح.','الإيمان والعمل الصالح',1),
	 (NULL,'التركيز على الآخرة يجعل الدنيا تأتي تبعًا، لأن الله ييسر أمور من يسعى للدار الآخرة بصدق.',NULL,'اجعل الآخرة همك، تكفيك الدنيا وما فيها.',' السعي للآخرة',1),
	 (NULL,'كل شيء في الكون مقدر من الله بحكمته ورحمته، ولا يحدث شيء إلا بإذنه ومشيئته.',NULL,'إنا كل شيء خلقناه بقدر.','القضاء والقدر',1),
	 (NULL,'المصائب والبلاء من حكم الله في خلقه، وهي لابتلاء العباد واختبارهم وتمييزهم.',NULL,'وما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها.','حكمة البلاء',1),
	 (NULL,'الله يهدي من يشاء ويضل من يشاء بحكمته ورحمته، والهداية من نعمه والضلال من عقوبته.',NULL,'من يهد الله فهو المهتدي ومن يضلل فلن تجد له ولياً مرشداً.','الهداية والضلال',1),
	 (NULL,'العبد مسؤول عن اختياره وعمله، والله أعطاه الحرية والاختيار ليكون مسؤولاً عن أفعاله.',NULL,'كل نفس بما كسبت رهينة.','المسؤولية والاختيار',1),
	 (NULL,'الرضا بقضاء الله وقدره يورث في القلب السكينة والطمأنينة، لأنه يعلم أن الله حكيم رحيم.',NULL,'رضيت بالله رباً وبالإسلام ديناً وبمحمد نبياً.','الرضا بالقدر',1),
	 (NULL,'التوكل على الله يورث في القلب الثقة والسكينة، لأنه يعلم أن أموره كلها بيد الله الحكيم.',NULL,'ومن يتوكل على الله فهو حسبه.','التوكل على الله',1),
	 (NULL,'الصبر على البلاء يورث الأجر والثواب، ويقوي الإيمان ويزيد اليقين بالله.',NULL,'إنما يوفى الصابرون أجرهم بغير حساب.','الصبر على البلاء',1),
	 (NULL,'الشكر على النعم يزيدها ويباركها، والجزع على المصائب لا يغير من قدر الله شيئاً.',NULL,'لئن شكرتم لأزيدنكم ولئن كفرتم إن عذابي لشديد.','الشكر والرضا',1),
	 (NULL,'الله حكيم في خلقه وأمره، وكل شيء يحدث في الكون له حكمة بالغة يعلمها هو.',NULL,'وما خلقت الجن والإنس إلا ليعبدون.','حكمة الخلق',1);
